return items.map(item => {
    let input;
    try {
        input = JSON.parse(item.json.text);
    } catch (e) {
        return {
            json: {
                error: `Invalid JSON: ${e.message}`,
                raw: item.json.text
            }
        };
    }

    let output = [];

    // --- College-level summary document sentence ---
    const college_doc = `In ${input.placement_year}, ${input.college_name} placed ${input.total_students_placed} out of ${input.total_students_registered} students (${input.placement_percentage}%) with highest package ${input.highest_package_text}, average package ${input.average_package_text}, and median package ${input.median_package_text}. Top recruiters include ${input.top_recruiters.join(", ")}.`;

    output.push({
        json: {
            type: "college_summary",
            college_name: input.college_name,
            placement_year: input.placement_year,
            total_students_registered: input.total_students_registered,
            total_students_placed: input.total_students_placed,
            placement_percentage: input.placement_percentage,
            highest_package_text: input.highest_package_text,
            average_package_text: input.average_package_text,
            median_package_text: input.median_package_text,
            international_offers: input.international_offers,
            ppo_offered: input.ppo_offered,
            ppo_accepted: input.ppo_accepted,
            highest_package_lpa: input.highest_package_lpa,
            average_package_lpa: input.average_package_lpa,
            median_package_lpa: input.median_package_lpa,
            top_recruiters: input.top_recruiters,
            document_text: college_doc,
            metadata: {
                sector_focus: "Engineering & IT",
                data_source: "IIT Madras Placement Report 2025"
            }
        }
    });

    // --- Branch-wise summaries ---
    if (Array.isArray(input.branch_wise_placements)) {
        input.branch_wise_placements.forEach(branch => {
            const branch_doc = `${branch.program_level} in ${branch.branch} had a placement rate of ${branch.placement_rate * 100}%, with highest package ${branch.highest_package_text} and average package ${branch.average_package_text}.`;

            output.push({
                json: {
                    type: "branch_summary",
                    college_name: input.college_name,
                    placement_year: input.placement_year,
                    program_level: branch.program_level,
                    branch: branch.branch,
                    placement_rate: branch.placement_rate,
                    average_package_text: branch.average_package_text,
                    highest_package_text: branch.highest_package_text,
                    average_package_lpa: branch.average_package_lpa,
                    highest_package_lpa: branch.highest_package_lpa,
                    // ✅ Removed source_text
                    document_text: branch_doc,
                    metadata: {
                        program_type: branch.program_level,
                        data_source: "IIT Madras Placement Report 2025"
                    }
                }
            });
        });
    }

    return output;
}).flat();

