const match = raw.match(/```json([\s\S]*?)```/);

if (!match) {
  return [
    {
      json: {
        error: "No JSON block found",
        raw_sample: raw.slice(0, 200)
      }
    }
  ];
}

let parsed;
try {
  parsed = JSON.parse(match[1].trim());
} catch (err) {
  return [
    {
      json: {
        error: "Failed to parse JSON",
        details: err.message,
        raw_json: match[1].trim().slice(0, 200)
      }
    }
  ];
}

// Return each item separately
return parsed.map(obj => ({ json: obj }));

